$(function () {
    console.log("site is ready for js!!!");
    
    $("a").attr("tabindex", -1);
    
    
    $(window).bind('DOMMouseScroll mousewheel', function (e) {
        function scroll(obj, e) {
            if (obj === null) {
                console.log("Nothing scroll");
                return;
            }
            var evt = e.originalEvent;
            var direction = evt.detail ? evt.detail * (-120) : evt.wheelDelta;
            
            if (direction > 0) {
                direction = obj.scrollLeft() - 120;
            }
            else {
                direction = obj.scrollLeft() + 120;
            }
            
            obj.scrollLeft(direction);
            obj.scrollTop(direction);
            
            e.preventDefault();
        }
        
        function scrollableRecursive($current, dir, e) {
            var $closest = $current.closest('.scroll');
            if ($closest.length === 0) return null;
            
            var max, cur, progress, space;
            var direction = typeof e.deltaY === "undefined" ? evt.wheelDelta : -e.deltaY;//evt.detail ? evt.detail * (-120) : evt.wheelDelta;
            
            var el = $closest[0];
            
            space = el.scrollHeight - el.clientHeight;
            progress = el.scrollTop / space;
            if (space > 0 && ((dir < 0 && progress > 0) || (dir > 0 && progress < 1))) {
                $closest.scrollTop($closest.scrollTop() - direction);
                return $closest;
            }
            
            space = el.scrollWidth - el.clientWidth;
            progress = el.scrollLeft / space;
            if (space > 0 && ((dir < 0 && progress > 0) || (dir > 0 && progress < 1))) {
                $closest.scrollLeft($closest.scrollLeft() - direction);
                return $closest;
            }
            
            return scrollableRecursive($closest.parent(), dir, e);
        }
        
        
        // текущий див
        var $trg = $(e.target);
        
        // событие
        var evt = e.originalEvent;
        
        // направление
        var direction = evt.detail ? -evt.detail : evt.wheelDelta / 120;
        
        // двигаем
        scrollableRecursive($trg, -direction, evt);
        
        //        console.log(evt);
        
        e.preventDefault();
    });
    
    document.fullscreenEnabled = document.fullscreenEnabled || document.mozFullScreenEnabled || document.documentElement.webkitRequestFullScreen;
    
    function requestFullscreen(element) {
        if (element.requestFullscreen) {
            element.requestFullscreen();
        } else if (element.mozRequestFullScreen) {
            element.mozRequestFullScreen();
        } else if (element.webkitRequestFullScreen) {
            element.webkitRequestFullScreen(Element.ALLOW_KEYBOARD_INPUT);
        }
    }
    
    if (document.fullscreenEnabled) {
        requestFullscreen(document.documentElement);
    }
});

var ChatMessage = function(author, text) {
    this.text = text;
	this.tags = this.getTags();
    this.author = author;
};

ChatMessage.prototype.getHtml = function () {
    var tmpl = '' +
    '<div class="chat-panel__list__item layout horizontal center">' +
        '<div class="avatar avatar_small">' +
            '<img src="{0}" alt="" />' +
        '</div>' +
        '<div class="chat-panel__list__item__content flex">' +
            '<p>{1}: {2}</p>' +
        '</div>' +
    '</div> ';

    return String.format(tmpl, this.getAvatarSrc(), this.getNickname(), this.getHtmlText());
};

ChatMessage.prototype.getAvatarSrc = function () {
    var url = $.cloudinary.url(this.author.avatar.public_id, { width: 100, height: 100, crop: "fill" });
    return url;
};

ChatMessage.prototype.getNickname = function() {
    var nickTmpl = '<a href="/profile/{0}" class="user">{1}</a>';
    return String.format(nickTmpl, this.author._id, this.author.name.first + " " + this.author.name.last);
};

ChatMessage.parseText = function(text) {
    // template for wrap every tag inside text
    var tagTmpl = '<a href="/tag/$1" class="tag">$1</a>';
    // regex patter to find every words that starts with #
    var regexp = new RegExp('#([^\\s]+)', 'g');
    // find hashtags inside text
    var text = text.replace(regexp, tagTmpl);
    // return final string
    return text;
};

ChatMessage.prototype.getHtmlText = function() {
    return ChatMessage.parseText(this.text);
};

ChatMessage.prototype.getTags = function() {
	var regexp = new RegExp('#([^\\s]+)', 'g');
	return this.text.match(regexp);
}

// Chat class
var Chat = function () {
    this.messages = [];
    this.socket = io();

    (function setupSocket() {
        var chat = this;
        // register in chat server
        chat.socket.emit('chat.assign', chatUserToken);

        // listen socket messages
        chat.socket.on('chat.message', function (user, msg) {
            // work with chat message received from server
            chat.addMessage(user, msg); 
        });
    }.bind(this))();
};

Chat.prototype.addMessage = function (author, text) {
    var message = new ChatMessage(author, text);
    this.messages.push(message);
    $.publish('chat', message);
};

Chat.prototype.sendMessage = function(text) {
    // parse tags inside text helper
    var getChatTags = function(rawChat) {
        var regexp = new RegExp('#([^\\s]+)', 'g');
        var tags = rawChat.match(regexp);

        if (tags && tags.length) {
            tags = tags.map(function(s) { return s.slice(1); });
        }

        return tags;
    };

    // collect every tag inside chat text
    var tags = getChatTags(text);

    // send message to server
    this.socket.emit('chat.message', tags, text);
};


var ChatInput = function($obj, tags) {
    this.el = $obj;
    this.tags = tags || [];
};

ChatInput.prototype.getBaseString = function() {
    final = "";
    for (var i = 0; i < this.tags.length; i++) {
        var tag = this.tags[i];
        final += "#" + tag + " ";
    }

    return final;
};

ChatInput.prototype.getMessage = function() {
    return this.el.val();
};

ChatInput.prototype.reset = function() {
    this.el.val(this.getBaseString());
};

ChatInput.prototype.isClean = function() {
    return this.el.val() === this.getBaseString();
};

ChatInput.prototype.isFocus = function() {
    return this.el.is(":focus");
};

ChatInput.prototype.focusOut = function() {
    this.el.focusout();
};

ChatInput.prototype.focusIn = function() {
    this.el.focus();
};

$(function () {
    $.subscribe('chat', function (_, message) {
		$('.chat-panel').each(function(i, panel) { 
			var dataTags = $(panel).data('chat-tag');
			if(dataTags) {
				var panelTags = $(panel).data('chat-tag').split(";");
				for (var i in message.tags) {
					if (panelTags.indexOf(message.tags[i]) !== -1) {
						var list = $(panel).find('.chat-panel__list');
						list.prepend(message.getHtml());
						return true;
					}
				}
			} else {
				var list = $(panel).find('.chat-panel__list');
				list.prepend(message.getHtml());
			}
		});
        //
        //$('.chat-panel__list').prepend($(message.getHtml()));
    });

    var chat = new Chat();
    var chatInput = new ChatInput($("#chat"));

    // setup ui for send msg
    // catch enter key event
    shortcut.add('enter', function () {
        if (chatInput.isFocus() && !chatInput.isClean()) {
            chat.sendMessage(chatInput.getMessage());
            chatInput.reset();
        } else {
            chatInput.focusIn();
        }
    });

    // catch click on button
    $('.chat__button').click(function () {
        if (!chatInput.isClean()) {
            chat.sendMessage(chatInput.getMessage());
            chatInput.reset();
        }
        return false;
    });

    $('.chat-panel').livequery(
        function() {
            // load by ajax last 10 messages
			var container = $(this).find('.chat-panel__list');
            var tags = $(this).data('chat-tag');//.split(";");
			console.log(tags);
            var url = "/json/chat";
    
            var query = {
                func: "messages"
            };
    
            if (tags) { query["tags"] = tags; }
    
            $.get(url, query).done(function (data) {
				console.log(data);
				if (data.data && data.data.results) {
					for (var i = 0; i < data.data.results.length; i++) {
						console.log(data.data.results[i]);
						var message = new ChatMessage(data.data.results[i].author, data.data.results[i].text);
						container.append(message.getHtml());
						
					}
				}
			});
        }
    );

});

$(function() {
    $.fn.media = function () {
        this.each(function () {
            var $mediaList = $(this);
            var $container = $mediaList.parent();
            var $items = $mediaList.find('.masonry__list_item');
            var $mega = $items.filter(function () { return $(this).hasClass('item_mega'); });
            
            var containerHeight = $container.height();// - 20;
            $mediaList.height(containerHeight);
            
            var small = containerHeight < 600;
            var baseHeight = containerHeight / 6;//0.15;
            var baseWidth = baseHeight / 0.5625;
            
            var index = 0;
            $items.each(function () {
                var $item = $(this);
                $item.height(baseHeight * (2 + (small ? 1 : 0)));
                $item.width(baseWidth * (2 + (small ? 1 : 0)));
                $item.attr("tabindex", index++);
            });
            
            $mega.each(function () {
                var $item = $(this);
                $item.height(baseHeight * (4 + (small ? 2 : 0)));
                $item.width(baseWidth * (4 + (small ? 2 : 0)));
            });
            
            $mediaList.isotope({
                layoutMode: 'masonryHorizontal',
                masonryHorizontal: {
                    rowHeight: baseHeight
                },
                itemSelector: '.masonry__list_item'
            });
        });
    };
    
    $(".masonry__list").livequery(
        function () {
            var masonry = $(this);
            masonry.media();

            $(window).resize(function () {
                masonry.media();
            });
        },
        function () {

        }
    );
});
$(function () {
    var $live = $('.live__page');
    $live.livequery(
        function () {
            console.log("LIVE PAGE");
            var live = function() {
                $(this).width(5000);
                $(this).find('.live__camera').each(
                    function () {
                        var $cam = $(this);
                        var isMain = $cam.is(".live__camera_main");
                        var isAlt = $cam.is(".live__camera_alt");
                        var $frame = $cam.find("object");
                        var container = $cam.parent();
                        var height = container.height();
                        var width = (height - 31) * (16 / 9);
                        
                        $frame.height(height).width(width);
                        $cam.height(height).width(width);
                        
                        if (isAlt) {
                            $cam.parent().width(width).parent().width(width);
                        }
                    }
                );
                
                var $l = $(this);
                var width = 0;
                
                $l.children().each(function () {
                    var $child = $(this);
                    width += parseInt($child.css('width'));
                });
                
                $l.width(width);
            }.bind(this);

            live();
            
            $(window).resize(function () {
                live();
            });
        },
        function () {
            
        }
    );
});
$(function () {
    var loadContentAsync = function(url) {
        $("#content").load(url + " #content > *");
    }
    $("a").livequery(
        function() {
            $(this).click(function () {
                var url = $(this).attr('href');
                loadContentAsync(url);
                window.history.pushState({ url: url }, document.title, url);
                return false;
            });
        }, 
        function() {
            
        }
    );

    $(document).on({
        ajaxStart: function () {
            var loadingTmpl = 
                '<div class="flex layout vertical center">' +
                    '<div class="flex"></div>' +
                    '<div class="whirly-loader center">' +
                        'Loading…'+
                    '</div>' +
                    '<div class="flex"></div>' +
                '</div>';
            $("#content").html(loadingTmpl);
        },
         ajaxStop: function() {
             
        }
    });

    window.addEventListener('popstate', function (event) {
        loadContentAsync(event.state.url);
    });

    
    window.history.replaceState({ url: document.location.href }, document.title, document.location.href);
});